/**
 * Get Google Fonts formatted for dropdown menus.
 */
export function getGoogleFontOptions() {
    return GOOGLE_FONTS.map(f => ({
        value: f.value,
        label: f.label,
        category: f.category
    }));
}
/**
 * Get a Google Font by name.
 */
export function getGoogleFontByName(name) {
    return GOOGLE_FONTS.find(f => f.name === name);
}
/**
 * Get all unique font names (for preloading all fonts).
 */
export function getAllGoogleFontNames() {
    return GOOGLE_FONTS.map(f => f.name);
}
